    public static Aviso Entrada(String xmlDocument) {

        Aviso aviso = new Aviso();
        //ReporteAvisoConsolidado reporte = null;
        Schema schema;
        JAXBContext jaxbContext;
        Unmarshaller unMarshaller;
        SchemaFactory schemaFactory;
        JAXBElement<Aviso> AvisoTypeElement;
        ByteArrayInputStream input;
        File file;

        try {
            jaxbContext = JAXBContext.newInstance("www.bcie.org.avisovencimiento");
            unMarshaller = jaxbContext.createUnmarshaller();
            schemaFactory = SchemaFactory.newInstance("http://www.w3.org/2001/XMLSchema");
            file = new File("./src/META-INF/AvisoConsolidado.xsd");
            schema = schemaFactory.newSchema(file);
            unMarshaller.setSchema(schema);

            input = new ByteArrayInputStream(xmlDocument.getBytes("UTF-8"));
            AvisoTypeElement = (JAXBElement<Aviso>) unMarshaller.unmarshal(input);
            aviso = AvisoTypeElement.getValue();

            // reporte = new ReporteAvisoConsolidado(aviso);
            //String reporteBase64 = reporte.generarReporteAvisoConsolidado();
            //System.out.println(reporteBase64);
        } catch (JAXBException e) {
            /*
            e.printStackTrace();
              } catch (SAXException e) {
           */
            //e.printStackTrace();
        } catch (Exception e) {
            //logger.error(e);
            e.printStackTrace();
        }
        return aviso;
    }

